mapper.select:
select post.id,post.title,post.comment_count,post.recommend,post.status,post.level,post.created,post.integral, user.username, user.vip_level,user.avatar from post,user
post.level = 1 and post.user_id = user.id
order by created desc limit #{current},#{size};

select post.id,post.title,post.content,post.comment_count,post.recommend,post.status,post.level,post.created,post.integral, user.username,user.vip_level,user.avatar from post,user
post.user_id = user.id
order by created desc limit #{current},#{size};

SELECT id,title,comment_count FROM post

    DATE_FORMAT(created,'%Y-%m-%d') BETWEEN (SELECT DATE_SUB(CURDATE(),INTERVAL 6 DAY)) AND (SELECT CURDATE())

ORDER BY comment_count DESC LIMIT 0,10;
